import React from "react";
import { Routes, Route, Navigate } from "react-router-dom"; // Make sure to import Route and Navigate

import Main from "./components/Mainhome";
import Signup from "./components/Signup";
import Login from "./components/Login";
import PrivateRoute from "./PrivateRoute";
import MainTwo from "./components/Mainhome2/MovieDetails";
import Admin from "./components/Admin/Admin";
import Home from "./components/Mainhome";

function App() {
  let routes = (
    <>
         <Route index path="/" element={<Main />} />
      <Route path="/signup" element={<Signup />} />
      <Route path="/login" element={<Login />} />
      <Route path="/Main" element={<Main2/>} />



    </>
  )

  const user = localStorage.getItem("token");
  if(user){
    routes = (
      <>
      <Route path="/home" element={<Main />} />
      {/* <Route path="/admin" component={Admin} /> */}

      </>
    )
  }
  return (
    <>

    <Routes>
      {routes}
    </Routes>
    </>

  );
}

export default App;
