import React from "react";
import { Route, Routes, Navigate } from "react-router-dom";
import Home from "./components/home";
import Signup from "./components/Signup";
import Login from "./components/Login";
import Movies from "./components/Moviespage/MovieDetails";

import Admin from "./components/Admin/Admin"; // Import the new component
import PrivateRoute from "./PrivateRoute";


function App() {
  const user = localStorage.getItem("token");

  return (
    <Routes>

<Route path="/signup" element={<Signup />} />
    <Route path="/login" element={<Login />} />
   <Route path="/" element={<PrivateRoute />}>
   <Route path="/public/movies" element={<Home />} />
      <Route path="/movie/:_id" element={<Movies />} />
      <Route path="/admin" element={<Admin />} />
   </Route>



      {/* Add the new route for the movie details page */}
      {/* <Route path="/movie" element={<Movies />} /> */}

    </Routes>
  );
}
export default App;



