import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "./AdminHome.module.css"; // Import the CSS file for styling

const AdminHome = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const url = "https://eventmanagement.loca.lt/api/events"; // Replace with your API endpoint
      const { data } = await axios.get(url);
      setEvents(data);
    } catch (error) {
      console.error("Error fetching events:", error);
    }
  };

  const handleCreateEvent = () => {
    // Implement the logic to handle create event here
    console.log("Create Event button clicked");
  };

  const handleDeleteEvent = () => {
    // Implement the logic to handle delete event here
    console.log("Delete Event button clicked");
  };

  return (
    <div className={styles.adminHomeContainer}>
      <h1 className={styles.heading}>Admin Home Page</h1>
      <div className={styles.buttonsContainer}>
        <button className={styles.createEventBtn} onClick={handleCreateEvent}>
          Create Event
        </button>
        <button className={styles.deleteEventBtn} onClick={handleDeleteEvent}>
          Delete Event
        </button>
      </div>
      {/* Display the fetched events data */}
      <div className={styles.eventsContainer}>
        <h2>Events:</h2>
        <ul>
          {events.map((event) => (
            <li key={event.id}>
              <h3>{event.name}</h3>
              <p>Date: {event.date}</p>
              <p>Description: {event.description}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminHome;
