import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "./styles.module.css";

const Admin = () => {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({
    name: "",
    date: "",
    description: "",
  });

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const url = "http://your-api-url/api/events"; // Replace with your API endpoint
      const { data } = await axios.get(url);
      setEvents(data);
    } catch (error) {
      console.error("Error fetching events:", error);
    }
  };

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      const url = "http://your-api-url/api/events"; // Replace with your API endpoint
      await axios.post(url, newEvent);
      fetchEvents();
      setNewEvent({ name: "", date: "", description: "" });
    } catch (error) {
      console.error("Error creating event:", error);
    }
  };

  const handleDeleteEvent = async (eventId) => {
    try {
      const url = `http://your-api-url/api/events/${eventId}`; // Replace with your API endpoint
      await axios.delete(url);
      fetchEvents();
    } catch (error) {
      console.error("Error deleting event:", error);
    }
  };

  return (
    <div className={styles.adminContainer}>
      <h1 className={styles.adminHeading}>Admin Page</h1>

      {/* Create Event Form */}
      <h2 className={styles.subHeading}>Create Event</h2>
      <form className={styles.createEventForm} onSubmit={handleCreateEvent}>
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
          required
        />
        <input
          type="date"
          value={newEvent.date}
          onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
          required
        />
        <textarea
          placeholder="Event Description"
          value={newEvent.description}
          onChange={(e) =>
            setNewEvent({ ...newEvent, description: e.target.value })
          }
          required
        />
        <button type="submit">Create Event</button>
      </form>

      {/* Event List */}
      <h2 className={styles.subHeading}>Event List</h2>
      <ul className={styles.eventList}>
        {events.map((event) => (
          <li key={event.id}>
            <h3>{event.name}</h3>
            <p>Date: {event.date}</p>
            <p>Description: {event.description}</p>
            <button onClick={() => handleDeleteEvent(event.id)}>
              Delete Event
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Admin;
