import React from "react";
import { useParams } from "react-router-dom";
import styles from "./styles.modules.css";

const MovieDetails = () => {
  // Get the movie ID from the URL
  const { movieId } = useParams();

  // Replace this with your movie data
  const movieData = {
    id: "1",
    name: "Baby (2023)",
    image: "https://example.com/baby.jpg",
    shows: ["1st show", "2nd show"],
    startEnd: ["10:00 am - 11:00 am", "12:00 pm - 1:00 pm"],
    capacity: [50, 40],
    price: [100, 80],
  };

  // Find the movie details based on the ID
  const movie = movieData.id === movieId ? movieData : null;

  // If the movie is not found, display a message
  if (!movie) {
    return <div>Movie not found</div>;
  }

  return (
    <div className={styles.movieDetailsContainer}>
      <h2>{movie.name}</h2>
      <img src={movie.image} alt={movie.name} className={styles.movieImage} />
      <div>
        <h3>Shows:</h3>
        {movie.shows.map((show, index) => (
          <p key={index}>{show}</p>
        ))}
      </div>
      <div>
        <h3>Start / End:</h3>
        {movie.startEnd.map((time, index) => (
          <p key={index}>{time}</p>
        ))}
      </div>
      <div>
        <h3>Capacity:</h3>
        {movie.capacity.map((cap, index) => (
          <p key={index}>{cap}</p>
        ))}
      </div>
      <div>
        <h3>Price:</h3>
        {movie.price.map((price, index) => (
          <p key={index}>{price}rs</p>
        ))}
      </div>
    </div>
  );
};
export default MovieDetails;
