import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styles from "./styles.module.css";

const Main = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMoviesFromAPI = () => {
      // Replace 'API_ENDPOINT' with the actual API endpoint URL to fetch movie data
      fetch("http://localhost:3000/public/movies")
        .then((response) => response.json())
        .then((data) => setMovies(data))
        .catch((error) => console.error("Error fetching movie data:", error));
    };

    fetchMoviesFromAPI();
  }, []);

  return (
    <div className={styles.homeContainer}>
      <h1 className={styles.appName}>Movie Club</h1>
      <div className={styles.movieList}>
        {movies.map((movie) => (
          <Link to={`/movie/${movie.id}`} key={movie.id} className={styles.movieItem}>
            <img src={movie.image} alt={movie.name} className={styles.movieImage} />
            <h3 className={styles.movieName}>{movie.name}</h3>
            <p className={styles.movieRating}>Rating: {movie.rating}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Main;
