import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styles from "./styles.module.css";

const Home = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMoviesFromAPI = () => {
      fetch("https://eventmanagement.loca.lt/movies") // Replace with your actual API endpoint
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
       .then((data) => {
          console.log(data);
          setMovies(data.data);
        })
        .catch((error) => console.error("Error fetching movie data:", error));
    };

    fetchMoviesFromAPI();
  }, []);
console.log()
  return (
    <div className={styles.homeContainer}>
      <h1 className={styles.appName}>Movie Club</h1>
      <div className={styles.movieList}>
        {movies.map((movie) => (

          <Link to={`/movie/${movie.id}`} key={movie.id} className={styles.movieItem}>
            {console.log(movie,"each movie")}
            <img src={movie.imgLink} alt={movie.name} className={styles.movieImage} />
            <h3 className={styles.movieName}>{movie.name}</h3>
            <p className={styles.movieRating}>Rating: {movie.rating}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Home;

