// AdminHome.js

import React, { useState } from "react";
import axios from "axios";
import styles from "./AdminHome.module.css";
import DeleteEventModal from "./DeleteEventModal";
import Logout from "./Logout";

const AdminHome = () => {
  const [formData, setFormData] = useState({
    name: "",
    start_time: "",
    imgLink: "",
    rating: "",
    genre: "",
    capacity: "",
    price: "",
  });
  const [showForm, setShowForm] = useState(false);

  // State to manage the visibility of the DeleteEventModal
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [eventToDelete, setEventToDelete] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCreateEvent = async (e) => {
    try {
      let token = localStorage.getItem('token');
      e.preventDefault();
      const url = "https://eventmanagement.loca.lt/admin/create-event"; // Replace with your API endpoint for creating events
      let headers = {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + token
      };

      const response = await axios.post(url, formData, { headers });
      console.log("Event created:", response.data);
      if (response.data) {
        setShowForm(false);
        alert("Event created successfully");

        // Refresh the page after successful event creation
        window.location.reload();
      }

    } catch (error) {
      console.error("Error creating event:", error);
      alert("Event not created" + error);
    }
  };

  const handleDeleteEvent = async (eventId) => {
    try {
      const url = `https://eventmanagement.loca.lt/admin/cancel/${eventId}`;
      // Replace with your API endpoint for deleting events
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': 'Bearer ' + token
      };
      await axios.delete(url, { headers });
      
      // Close the DeleteEventModal after successful deletion
      setShowDeleteModal(false);

      // Optionally, you can refresh the page after successful event deletion
      window.location.reload();
    } catch (error) {
      console.error("Error deleting event:", error);
    }
  };

  return (
    <div className={styles.adminHomeContainer}>
      <div className={styles.topBar}>
        <h1 className={styles.heading}>Admin HomePage</h1>
        <Logout />
      </div>
      <div className={styles.buttonsContainer}>
        <button className={styles.createEventBtn} onClick={() => setShowForm(true)}>
          Create Event
        </button>
        <button className={styles.deleteEventBtn} onClick={() => setShowDeleteModal(true)}>
          Delete Event
        </button>
      </div>
      {showForm && (
        <form className={styles.eventForm}>
          {/* Form fields for creating events */}
          {/* ... (input fields and submit button) */}
        </form>
      )}

      {/* Render the DeleteEventModal if showDeleteModal is true */}
      {showDeleteModal && (
        <DeleteEventModal
          eventToDelete={eventToDelete}
          onCancel={() => setShowDeleteModal(false)}
          onDelete={handleDeleteEvent}
        />
      )}
    </div>
  );
};

export default AdminHome;
