import React, { useState } from "react";
import axios from "axios";
import styles from "./AdminHome.module.css";
import Logout from "./logout";

import createEventIcon from "./create event.png";
import deleteEventIcon from "./delete event.jpg";

const AdminHome = () => {
  const [formData, setFormData] = useState({
    name: "",
    start_time: "",
    imgLink: "",
    rating: "",
    genre: "",
    capacity: "",
    price: "",
  });
  const [showForm, setShowForm] = useState(false);
  const [createSuccess, setCreateSuccess] = useState(false); // State variable for create success message
  const [deleteSuccess, setDeleteSuccess] = useState(false); // State variable for delete success message

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCreateEvent = async (e) => {
    try {
      let token = localStorage.getItem('token');
      e.preventDefault();
      const url = "https://eventmanagement.loca.lt/admin/create-event"; // Replace with your API endpoint for creating events
      let headers = {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + token
      };

      const response = await axios.post(url, formData, { headers });
      console.log("Event created:", response.data);
      if (response.data) {
        setShowForm(false);
        setCreateSuccess(true); // Show create success message
        setTimeout(() => setCreateSuccess(false), 3000); // Hide success message after 3 seconds

        // Refresh the page after successful event creation
        window.location.reload();
      }

    } catch (error) {
      console.error("Error creating event:", error);
      alert("Event not created" + error);
    }
  };

  const handleDeleteEvent = async () => {
    try {
      const eventId = prompt("Enter the ID of the event to delete:");
      if (!eventId) return;

      const url = `https://jolly-moth-25.loca.lt/admin/cancel/${eventId}`; // Replace with your API endpoint for deleting events
      const response = await axios.delete(url);
      console.log("Event deleted:", response.data);
      setDeleteSuccess(true); // Show delete success message
      setTimeout(() => setDeleteSuccess(false), 3000); // Hide success message after 3 seconds
    } catch (error) {
      console.error("Error deleting event:", error);
    }
  };

  return (
    <div className={styles.adminHomeContainer}>
      <div className={styles.topBar}>
        <h1 className={styles.heading}>Admin HomePage</h1>
        <Logout />
      </div>
      <div className={styles.buttonsContainer}>
        <button className={styles.createEventBtn} onClick={() => setShowForm(true)}>
        <img src={createEventIcon} alt="Create Event" />
          Create Event
        </button>
        <button className={styles.deleteEventBtn} onClick={handleDeleteEvent}>
        <img src={deleteEventIcon} alt="Delete Event" />
          Delete Event
        </button>
      </div>
      {showForm && (
        <form className={styles.eventForm}>
          {/* Form fields for creating events */}
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            placeholder="Event Name"
          />
          {/* ... (other form fields) ... */}
          <button className={styles.submitBtn} onClick={(e) => handleCreateEvent(e)}>
            Submit
          </button>
        </form>
      )}

      {/* Show create success message if event is created successfully */}
      {createSuccess && (
        <div className={styles.successMessage}>Event created successfully!</div>
      )}

      {/* Show delete success message if event is deleted successfully */}
      {deleteSuccess && (
        <div className={styles.successMessage}>Event deleted successfully!</div>
      )}
    </div>
  );
};

export default AdminHome;
