import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import styles from "./styles.modules.css";
import { useLocation } from "react-router-dom";
const Movies = (props) => {
  // Get the movie ID from the URL
  const { movieId } = useParams();
  const location = useLocation()
const {data}=location.state
console.log(data,"data")
const [singleMovie,setSingleMovie]=useState(data)


  return (
    <div className={styles.movieDetailsContainer}>
      <h2>{singleMovie.name}</h2>
      <img src={singleMovie.imgLink} alt={singleMovie.name} className={styles.movieImage} />
      {/* <div>
        <h3>Shows:</h3>
        {singleMovie.shows.map((show, index) => (
          <p key={index}>{show}</p>
        ))}
      </div> */}
      <div className={styles.movieInfo}>
        <h3 className={styles.movieInfoItem}>Start :{singleMovie.start_time}</h3>
        {/* {singleMovie.startEnd.map((time, index) => (
          <p key={index}>{time}</p>
        ))} */}
      </div>
      <div className={styles.movieInfoItem}>
        <h3 className={styles.movieInfoItem}>Capacity:{singleMovie.capacity}</h3>
        {/* {singleMovie.capacity.map((cap, index) => (
          <p key={index}>{cap}</p>
        ))} */}
      </div>
      <div className={styles.movieInfoItem}>
        <h3>Price:{singleMovie.price}</h3>
        {/* {singleMovie.price.map((price, index) => (
          <p key={index}>{price}rs</p>
        ))} */}
      </div>
      <Link to={`/booking/${movieId}`} className={styles.bookTicketBtn}>
        Book Ticket
      </Link>
    </div>
  );
};
export default Movies;
